import { Card, Button } from "react-bootstrap";

const Event = ({ event, onBook, onLike }) => {
  return (
    <Card style={{ width: "18rem", margin: "10px" }}>
      <Card.Img
        variant="top"
        src={require(`/images/${event.image}`)}
      />
      <Card.Body>
        <Card.Title>{event.name}</Card.Title>
        <Card.Text>{event.description}</Card.Text>
        <p>Price: {event.price} DT</p>
        <p>Participants: {event.nbParticipants}</p>
        <p>Tickets: {event.nbTickets}</p>

        {event.nbTickets === 0 && (
          <p style={{ color: "red", fontWeight: "bold" }}>Sold Out</p>
        )}

        <Button
          variant="primary"
          disabled={event.nbTickets === 0}
          onClick={() => onBook(event.id)}
        >
          Book an event
        </Button>

        <Button
          variant={event.like ? "danger" : "success"}
          style={{ marginLeft: "10px" }}
          onClick={() => onLike(event.id)}
        >
          {event.like ? "Dislike" : "Like"}
        </Button>
      </Card.Body>
    </Card>
  );
};

export default Event;
